import "./reset.css";
import "./App.css";
import Header from "./Components/Header";
import Footer from "./Components/Footer";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Home from "./Pages/Home";
import Allkanji from "./Pages/Allkanji";
import Mykanji from "./Pages/Mykanji";
import Pairgame from "./Pages/Pairgame";
import { useState } from "react";

function App() {
    const [email, setEmail] = useState("");
    const [logInEmail, setLogInEmail]=useState("")

    function handleChangeEmail(event) {
        setEmail(event.target.value);
    }

    function logIn(){
        console.log("just ran the login!")
        setLogInEmail(email)
    }
    function logOut(){
        console.log("just ran log out!")

        setLogInEmail("")
    }

    return (
        <BrowserRouter>
            <div className="App">
                <Header logInEmail={logInEmail}/>
                <Routes>
                    <Route path="/" element={<Home />} />
                    <Route path="/allkanji" element={<Allkanji logIn={logIn} logInEmail={logInEmail} email={email} handleChangeEmail={handleChangeEmail} />} />
                    <Route path="/mykanji" element={<Mykanji logInEmail={logInEmail} />} />
                    <Route path="/pairgame" element={<Pairgame />} />
                </Routes>
                <Footer />
            </div>
        </BrowserRouter>
    );
}

export default App;
